INSERT INTO `wp_zbs_sys_email` (`ID`, `zbs_site`, `zbs_team`, `zbs_owner`, `zbsmail_active`, `zbsmail_id`, `zbsmail_deliverymethod`, `zbsmail_fromname`, `zbsmail_fromaddress`, `zbsmail_replyto`, `zbsmail_ccto`, `zbsmail_bccto`, `zbsmail_subject`, `zbsmail_body`, `zbsmail_created`, `zbsmail_lastupdated`) VALUES
(1, -1, -1, -1, 1, 0, '', '', '', '', '', '', '', '\n\n	\n		\n		\n		<title>##TITLE##</title>\n		\n		/* -------------------------------------\n				INLINED WITH https://putsmail.com/inliner\n		------------------------------------- */\n		/* -------------------------------------\n				RESPONSIVE AND MOBILE FRIENDLY STYLES\n		------------------------------------- */\n		@media only screen and (max-width: 620px) {\n			table[class=body] h1 {\n				font-size: 28px !important;\n				margin-bottom: 10px !important; }\n			table[class=body] p,\n			table[class=body] ul,\n			table[class=body] ol,\n			table[class=body] td,\n			table[class=body] span,\n			table[class=body] a {\n				font-size: 16px !important; }\n			table[class=body] .wrapper,\n			table[class=body] .article {\n				padding: 10px !important; }\n			table[class=body] .content {\n				padding: 0 !important; }\n			table[class=body] .container {\n				padding: 0 !important;\n				width: 100% !important; }\n			table[class=body] .main {\n				border-left-width: 0 !important;\n				border-radius: 0 !important;\n				border-right-width: 0 !important; }\n			table[class=body] .btn table {\n				width: 100% !important; }\n			table[class=body] .btn a {\n				width: 100% !important; }\n			table[class=body] .img-responsive {\n				height: auto !important;\n				max-width: 100% !important;\n				width: auto !important; }}\n		/* -------------------------------------\n				PRESERVE THESE STYLES IN THE HEAD\n		------------------------------------- */\n		@media all {\n			.ExternalClass {\n				width: 100%; }\n			.ExternalClass,\n			.ExternalClass p,\n			.ExternalClass span,\n			.ExternalClass font,\n			.ExternalClass td,\n			.ExternalClass div {\n				line-height: 100%; }\n			.apple-link a {\n				color: inherit !important;\n				font-family: inherit !important;\n				font-size: inherit !important;\n				font-weight: inherit !important;\n				line-height: inherit !important;\n				text-decoration: none !important; }\n			.btn-primary table td:hover {\n				background-color: #34495e !important; }\n			.btn-primary a:hover {\n				background-color: #34495e !important;\n				border-color: #34495e !important; } }\n\n				/* Wh adjustments for better emails (mc2 templates) */\n				img {\n					max-width: 100%;\n					height: auto;\n				}\n				\n        /* specific to invoicesent.html */\n        .customer-info .zbs-line-info {\n          text-align:right;\n        }\n		\n	\n	\n		<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"body\" style=\"border-collapse:separate;background-color:#f6f6f6;width:100%\">\n			<tr>\n				<td style=\"font-family:sans-serif;font-size:14px;vertical-align:top\">&nbsp;</td>\n				<td class=\"container\" style=\"font-family:sans-serif;font-size:14px;vertical-align:top;max-width:580px;padding:10px;width:580px\">\n					<div class=\"content\" style=\"max-width:580px;padding:10px\">\n						<table class=\"main\" style=\"border-collapse:separate;background:#fff;border-radius:3px;width:100%\">\n							<!-- START MAIN CONTENT AREA -->\n							<tr>\n								<td class=\"wrapper\" style=\"font-family:sans-serif;font-size:14px;vertical-align:top;padding:20px\">\n									##MSG-CONTENT##\n								</td>\n							</tr>\n							<!-- END MAIN CONTENT AREA -->\n						</table>\n						<!-- START FOOTER -->\n						<div class=\"footer\" style=\"clear:both;padding-top:10px;text-align:center;width:100%\">\n							<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse:separate;width:100%\">\n								<tr>\n									<td class=\"content-block\" style=\"font-family:sans-serif;font-size:14px;vertical-align:top;color:#999999;font-size:12px;text-align:center\" colspan=\"2\">\n										##BIZ-INFO##\n									</td>\n								</tr>\n								<tr>\n									<td class=\"content-block\" width=\"50%\" style=\"font-family:sans-serif;font-size:14px;vertical-align:top;color:#999999;font-size:12px;text-align:center;padding-top:40px;text-align:left\">\n										 ##UNSUB-LINE##\n									</td>\n									<td class=\"content-block powered-by\" style=\"font-family:sans-serif;font-size:14px;vertical-align:top;color:#999999;font-size:12px;text-align:center;padding-top:40px;text-align:right\">\n										##POWERED-BY##\n									</td>\n								</tr>\n							</table>\n						</div>\n						<!-- END FOOTER -->\n						<!-- END CENTERED WHITE CONTAINER -->\n					</div>\n				</td>\n				<td style=\"font-family:sans-serif;font-size:14px;vertical-align:top\">&nbsp;</td>\n			</tr>\n		</table>\n	\n', 1659691596, 1659691596),
(2, -1, -1, -1, 1, 1, '', '', '', '', '', '', 'Your Client Portal', '<strong>Your Client Portal</strong><br />\n<br />\nWelcome to your Client Portal. Your login details are:<br /><br />\n<ul>\n<li>Login link: ##PORTAL-LINK##</li>\n<li>Email: ##EMAIL##</li>\n<li>Password: ##PASSWORD##</li>\n</ul><br /><br />\nIn your client portal you can access your quotes and invoices, as well as pay invoices online using PayPal or Stripe. If you have any questions. Let us know.<br />\n<br />\n<strong>##EMAIL-FROM-NAME##</strong>', 1659691596, 1659691596),
(3, -1, -1, -1, 1, 2, '', '', '', '', '', '', 'Quote Accepted', '<h3 style=\"text-align:center;font-size: 1.5em;margin-top: 0em;margin-bottom: 2em\">Quote Accepted!</h3>\nHi there,\n<br /><br />\nThis is a notification email to let you know that your proposal <strong>##QUOTE-TITLE##</strong> has been accepted!\n<br /><br />\nHere is the link to view the proposal online: <a href=\"##QUOTE-URL##\">##QUOTE-URL##</a>\n<br /><br />\nAnd here is the link to edit the proposal:  <a href=\"##QUOTE-EDIT-URL##\">##QUOTE-EDIT-URL##</a>\n<br /><br />\nAwesome work!\n<br /><br />\n##EMAIL-FROM-NAME##', 1659691596, 1659691596),
(4, -1, -1, -1, 1, 3, '', '', '', '', '', '', 'You have received an Invoice', '<div class=\"container\" style=\"max-width:1200px;margin-left:auto;margin-right:auto\">\n  <div id=\"zbs_top_wrapper\">\n      <div id=\"zbs_invoice_logos\" style=\"width:50%;float:left\">\n          <div class=\"wh-logo-set ##LOGO-CLASS##\">\n              <img id=\"wh-logo-set-img\" src=\"##LOGO-URL##\" style=\"max-width:50%\" />\n          </div>\n      </div>\n      <div class=\"zbs-invoice-topper\" style=\"width: 50%;float: right;text-align: right\">\n          <table class=\"form-table\" style=\"text-align: right;float: right\">\n          <tr class=\"wh-large\">\n              <td><strong><label>##INVOICE-LABEL-INV-REF##</label></strong></td>\n              <td>##INVOICE-REF##</td>\n          </tr>\n          <tr class=\"wh-large\">\n              <td><strong><label>##INVOICE-LABEL-INV-DATE##</label></strong></td>\n              <td>##INVOICE-DATE##</td>\n          </tr>\n          <tr class=\"wh-large\">\n              <td><strong><label>##INVOICE-LABEL-DUE-DATE##</label></strong></td>\n              <td>##INVOICE-DUE-DATE##</td>\n          </tr>\n       </table>\n      </div>\n  </div>\n  <div class=\"clear\" style=\"clear:both\"></div>\n  <table style=\"width:100%\">\n    <tr>\n      <td style=\"width:50%\">\n        <div id=\"zbs-business-info-wrapper\">\n            <div class=\"business-info ##INVOICE-BIZ-CLASS##\">\n                ##BIZ-INFO##\n            </div>\n        </div>\n      </td>\n      <td style=\"width:50%\">\n        <div id=\"zbs-customer-info-wrapper\">\n            <div class=\"customer-info\">\n                ##INVOICE-CUSTOMER-INFO##\n            </div>\n        </div>\n      </td>\n    </tr>\n  </table>\n  <div class=\"clear\"></div>\n  <div id=\"zbs-invoice-items\" style=\"margin-top:2em\">\n      <table class=\"table\" style=\"width:100%\">\n          <thead style=\"border:1px solid #ccc;background:#f5f5f5;padding: 20px;line-height: 30px;font-weight: 100\">\n              ##INVOICE-TABLE-HEADERS##\n          </thead>\n          <div class=\"zbs-table-block\">\n              ##INVOICE-LINE-ITEMS##\n          </div>\n      </table>\n  </div>\n  ##INVOICE-TOTALS-TABLE##\n  </div>\n  ##PORTAL-VIEW-BUTTON##', 1659691596, 1659691596),
(5, -1, -1, -1, 1, 4, '', '', '', '', '', '', 'You have received a new Proposal', 'Hi there,\n<br /><br />\nThis is a notification email to let you know that your proposal <strong>##QUOTE-TITLE##</strong> is ready to view &amp; accept online.\n<br /><br />\nHere is the link to view the proposal: <a href=\"##QUOTE-URL##\">##QUOTE-URL##</a>\n<br /><br />\nPlease do let me know when you are ready to proceed, or if I can help you with anything else.\n<br /><br />\n##EMAIL-FROM-NAME##', 1659691596, 1659691596),
(6, -1, -1, -1, 1, 5, '', '', '', '', '', '', 'Your Event starts soon', '<h3 style=\"text-align:center;font-size: 1.5em;margin-top: 0em;margin-bottom: 2em;\">Your Event is starting soon</h3>\n<div style=\"text-align:center;font-size: 1.5em;margin-top: 0em;margin-bottom: 2em;\">##TASK-TITLE##</div>\n<br/>\n##TASK-BODY##\n##TASK-LINK##', 1659691596, 1659691596),
(8, -1, -1, -1, 1, 6, '', '', '', '', '', '', 'Your Client Portal Password', '<strong>Your Client Portal Password has been reset</strong><br />\n<br />\nYou can now access the client portal with the following information:<br />\n<ul>\n<li>Login link: ##PORTAL-LINK##</li>\n<li>Email: ##EMAIL##</li>\n<li>Password: ##PASSWORD##</li>\n</ul><br />\n<br />\n<strong>##EMAIL-FROM-NAME##</strong>', 1659691596, 1659691596),
(9, -1, -1, -1, 1, 7, '', '', '', '', '', '', 'Your Statement', '<strong>Your Statement for ##BIZ-NAME##</strong><br />\n<br />\nWe\'ve generated a statement for any invoices you have with ##BIZ-NAME## (attached to this email as a pdf).<br /><br />\nPlease do check this statement and pay any outstanding balances due.<br /><br /><br />\nYours Sincerely<br /><br />\n<strong>##EMAIL-FROM-NAME##</strong>', 1659691596, 1659691596);
